package explore.jwt;
import java.util.HashMap;
import java.util.Map;

import com.auth0.jwt.interfaces.DecodedJWT;

import explore.jwt.CreateToken;

public class App {

    public static void main(String[] args) {
        Map<String, Object> passportMap = new HashMap();
            passportMap.put("type", "<passport-visa-type>");
            passportMap.put("asserted", "<seconds-since-epoch>");
            passportMap.put("value", "<value-string>");
            passportMap.put("source", "<source-URL>");
        String jwt = CreateToken.sign("secret", passportMap);
        System.out.println(jwt);

        //try to verify
        DecodedJWT verifyJwt = CreateToken.verify(jwt,"secret");
        System.out.println(verifyJwt);

    }
}
